from selenium import webdriver
from selenium.webdriver.common.keys import Keys
from selenium.webdriver.common.by import By
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as EC
from pymongo import MongoClient
import time
import pickle


def selenium_scrap(path):
    # –ü–æ–¥–≥–æ—Ç–æ–≤–∫–∞ –¥—Ä–∞–π–≤–µ—Ä–∞
    driver = webdriver.Firefox(executable_path=r'./WebDriver/geckodriver')
    driver.maximize_window()
    driver.get(path)

    # –ê–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏—è
    login = driver.find_element_by_id('mailbox:login')
    login.send_keys('study.ai_172@mail.ru')
    submit_but = driver.find_element_by_id('mailbox:submit')
    submit_but.click()
    passw = WebDriverWait(driver, 10).until(EC.element_to_be_clickable((By.ID, 'mailbox:password')))
    passw.send_keys('NextPassword172')
    submit_but.click()

    # –ü–æ–¥–≥—Ä—É–∑–∫–∞ –≤—Å–µ—Ö –ø–∏—Å–µ–º
    mail_dataset = WebDriverWait(driver, 10).until(EC.presence_of_element_located((By.CLASS_NAME, 'dataset__items')))
    mail_list = set()
    while True:
        try:
            time.sleep(2)
            mail_recent_list = mail_dataset.find_elements_by_tag_name('a')
            for i in mail_recent_list:
                mail_list.add(i.get_attribute('href'))
            mail_recent_list[-1].send_keys(Keys.PAGE_DOWN)
        except:
            break

    # —Å–∫—Ä–∞–ø –ø–∏—Å–µ–º
    result = []
    print('[', end='')
    for message in mail_list:
        print('|', end='')
        if not message:
            continue
        if 'e.mail' in message:
            driver.get(message)
            body = WebDriverWait(driver, 10).until(EC.presence_of_element_located((By.CLASS_NAME, 'letter-body')))
            header = driver.find_element_by_class_name('letter__author')
            mes = {}
            mes['mes_from'] = header.find_element_by_tag_name('span').text
            mes['date'] = header.find_element_by_tag_name('div').text
            mes['title'] = driver.find_element_by_class_name('thread__subject').text
            mes['txt'] = body.text
            result.append(mes)
    print('] 100% –°–∫—Ä–∞–ø–∏–Ω–≥ –∑–∞–≤–µ—Ä—à–µ–Ω!')
    driver.close()
    return result


def main():
    # –°–∫—Ä–∞–ø–∏–Ω–≥
    path = 'https://mail.ru/'
    dataset = selenium_scrap(path)

    # —Ä–µ–∑–µ—Ä–≤–Ω–æ–µ –∫–æ–ø–∏—Ä–æ–≤–∞–Ω–∏–µ
    with open('1_mail.pickle', 'wb') as f:
        pickle.dump(dataset, f)

    ### –ü–æ–¥–≥–æ—Ç–æ–≤–∫–∞ –±–∞–∑—ã –¥–∞–Ω–Ω—ã—Ö
    client = MongoClient('192.168.1.6', 27017)  # –ø–æ–¥–∫–ª—é—á–µ–Ω–∏–µ –∫ –≤–∏—Ä—Ç—É–∞–ª—å–Ω–æ–π –º–∞—à–∏–Ω–µ
    db = client['mail']  # —É–∫–∞–∑–∞–Ω–∏–µ –±–∞–∑—ã –¥–∞–Ω–Ω—ã—Ö
    mail = db.mail  # —É–∫–∞–∑–∞–Ω–∏–µ –∫–æ–ª–ª–µ–∫—Ü–∏–∏
    mail.insert_many(dataset)  # –≤—Å—Ç–∞–≤–∫–∞ –≤ –ë–î
    print('–î–∞–Ω–Ω—ã–µ —É—Å–ø–µ—à–Ω–æ –¥–æ–±–∞–≤–ª–µ–Ω—ã –≤ MongoDB')
    for n in mail.find({}):
        print(n)


if __name__ == '__main__':
    main()

